import * as THREE from "three";
export function setupMockupScene(scene, mainObj) {
  let n = new THREE.Color(8108249),
    i = new THREE.Color(0),
    r = new THREE.Color("blue"),
    l = n.clone().lerp(r, 0.2).lerp(i, 0.25);
  scene.background = new THREE.Color(l);
  let ground = new THREE.PlaneGeometry(300, 200),
    s = new THREE.MeshStandardMaterial({ color: "white" }),
    d = new THREE.Mesh(ground, s);

  d.rotation.x = -Math.PI / 2;
  scene.add(d);

  let wall = new THREE.PlaneGeometry(250, 200),
    w = new THREE.MeshStandardMaterial({ color: "white" }),
    c = new THREE.Mesh(wall, w);

  c.position.set(0, 5, -10), scene.add(c);

  let wall2 = new THREE.PlaneGeometry(250, 200),
    w2 = new THREE.MeshStandardMaterial({ color: "white" }),
    c2 = new THREE.Mesh(wall2, w2);

  c2.rotation.y = -Math.PI / 2;
  c2.position.set(10, 2.5, 0), scene.add(c);
  scene.add(c2);

  mainObj.position.set(c2.position.x * 0.45, 0, c.position.z * 0.45);
  mainObj.rotation.z = Math.PI / 4;

  // let x = new THREE.DirectionalLight($, 0.5);
  // x.position.set(5, 10, 5),
  //   x.target.position.set(20, 0, -10),
  //   scene.add(x),
  //   scene.add(x.target);

  var ambientLight = new THREE.AmbientLight(0x333333);

  var hemiLight = new THREE.HemisphereLight(0xffffff, 0x444444);
  hemiLight.position.set(0, 20, 0);
  cena.add(ambientLight, hemiLight);

  var spotLight1 = new THREE.SpotLight(0x7a7572);
  spotLight1.position.set(100, 200, 500);

  var spotLight2 = new THREE.SpotLight(0x7a7572);
  spotLight2.position.set(-100, -200, -500);

  var dirLight = new THREE.DirectionalLight(0xbeb7b1);
  dirLight.position.set(500, 0, 400);

  cena.add(spotLight1, spotLight2, dirLight);
}
