import * as THREE from "three";
export function setupMockupScene(scene, mainObj) {
  let n = new THREE.Color(8108249),
    i = new THREE.Color(0),
    r = new THREE.Color("blue"),
    l = n.clone().lerp(r, 0.2).lerp(i, 0.25),
    $ = n.clone().lerp(r, 0.2);
  scene.background = new THREE.Color(l);
  let a = new THREE.PlaneGeometry(50, 50),
    s = new THREE.MeshStandardMaterial({ color: "white" }),
    d = new THREE.Mesh(a, s);

  (d.rotation.x = -Math.PI / 2)
    scene.add(d);

  let p = new THREE.PlaneGeometry(50, 40),
    w = new THREE.MeshStandardMaterial({ color: "white" }),
    c = new THREE.Mesh(p, w);

  c.position.set(0, 5, -20), scene.add(c);

  let p2 = new THREE.PlaneGeometry(50, 40),
    w2 = new THREE.MeshStandardMaterial({ color: "white" }),
    c2 = new THREE.Mesh(p2, w2);

  c2.rotation.y = -Math.PI / 2;
  c2.position.set(20, 5, 0), scene.add(c);
  scene.add(c2);

  mainObj.position.set(c2.position.x * 0.5, 0 , c.position.z *0.5);
  mainObj.rotation.z = Math.PI / 4;

  let x = new THREE.DirectionalLight($, 0.5);
  x.position.set(5, 10, 5),
    x.target.position.set(20, 0, -10),
    scene.add(x),
    scene.add(x.target);
}
